    private void jbtn_eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtn_eliminarActionPerformed
        // TODO add your handling code here:
        
        String id_prod = this.jtxt_idProd.getText().trim();
        if (id_prod.isEmpty()) {
            JOptionPane.showMessageDialog(null, "Ingrese un id v√°lido", "Validacion", JOptionPane.WARNING_MESSAGE);
            this.jtxt_idProd.requestFocus();
            return;
        }
        
            
        
        int op = JOptionPane.showConfirmDialog(null, "Seguro que desea eliminar el producto? " + id_prod, "Eliminando", 2);
        if (op == 0) {
            RegistroProducto reg = new RegistroProducto();
            reg.eliminarProducto(id_prod);
            JOptionPane.showMessageDialog(null,"Producto eliminado","Eliminando",1);
        } 
    }//GEN-LAST:event_jbtn_eliminarActionPerformed

